const initialState = {
    inputType : 1,
    inputText : "HelloWorld",
    expenses : [],
}

const reducer = (state = initialState, action) => {
    if(action.type === 'SET_INPUT_TYPE'){
        const newState = Object.assign([],state);
        newState.inputType = action.payload.inputType;
        return newState;
    }
    if(action.type === 'SET_INPUT_TEXT'){
        const newState = Object.assign([],state);
        newState.inputText = action.payload.inputText;
        return newState;
    }
    if(action.type === 'INSERT_EXPENSES'){
        if(state.inputText !== '' && state.inputType === 0) return state;
        const newState = Object.assign([],state);
        newState.expenses = [...newState.expenses,{
            id: newState.expenses[newState.expenses.length - 1].id + 1,
            text: newState.inputText,
            type: newState.inputType
        }]
        newState.inputType = 0;
        newState.inputText = '';
        console.log(newState);
        return newState
    }
    if(action.type === 'REMOVE_EXPENSE'){
        const newState = Object.assign([],state);
        newState.expenses = newState.expenses.filter(n => n !== action.payload.remove_id);
        console.log(newState);
        return newState
    }

    return state;
}

export default reducer
