import React,{useState} from 'react'
import {connect} from 'react-redux'
import Options from './Options'
import TextLine from './TextLIine'
function Form(props){
    return(
        <div>
            <Options clicked = {props.setInputType}/>
            <TextLine changed = {props.setInputText}/>
        </div>
    )
}
const mapDispatchToProps = dispatch =>{
    return{
        setInputType : (inputType) => dispatch({type : 'SET_INPUT', payload :{
            inputType : inputType
        }}),
        setInputText : inputText => dispatch({type : 'SET_INPUT_TEXT',payload : {
            inputText : inputText
        }})
    }
}
export default connect(null,mapDispatchToProps)(Form)
